import React, { useState } from 'react';
import { validateEmail } from '../../utils/helpers.js';

function ContactForm(){
    const [formState, setFormState] = useState({ name: '', email: '', message: '' });
    const { name, email, message } = formState;
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = e => {
        setFormState({ ...formState, [e.target]: e.target.value });
        if (e.target.name === 'email') {
            const isValid = validateEmail(e.target.value);
            if (isValid) {
                setErrorMessage('');
            }
            else {
                setErrorMessage('Please enter a valid email address');
            }
        } else {
            if (!e.target.value.length) {
                setErrorMessage(`${e.target.name} is required.`);
              } else {
                setErrorMessage('');
              }
        }
        if (!errorMessage) {
            setFormState({ ...formState, [e.target.name]: e.target.value });
          }

          console.log('errorMessage', errorMessage);
    };
    const handleSubmit = e => {
        e.preventDefault();

        console.log(formState, errorMessage);
    };

    return (
        <section>
            <h1>Contact Form</h1>
            <form id="contact-form" onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input type="text" name="name" onBlur={handleChange} defaultValue={name}/>
                <label htmlFor="email">Email</label>
                <input type="email" name="email" onBlur={handleChange} defaultValue={email} />
                <label htmlFor="message">Message</label>
                <textarea name="message" onBlur={handleChange} defaultValue={message}></textarea>
                {errorMessage && (
                    <div>
                        <p className="error-text">{errorMessage}</p>
                    </div>
                )}
                <button type="submit">Submit</button>
            </form>
        </section>
    );
}

export default ContactForm;
